const playbook = $json.hits?.hits?.[0]?._source || {};
const template = playbook.esql_template || '';

const webhookData = $node["Alert Webhook"].json;
const domain = webhookData.body?.domain || 'unknown.com';

return [
  {
    json: {
      query: template.replace('{{domain}}', domain),
      domain,
    },
  },
];

// Get the content from current item
const rawJsonString = $json.message?.content;

if (!rawJsonString) {
  throw new Error('OpenAI response content missing');
}

let data;
try {
  data = JSON.parse(rawJsonString);
} catch (error) {
  throw new Error('Failed to parse JSON from OpenAI response: ' + error.message);
}

// Risk color logic
const riskPercent = (data.risk_ratio ?? 0) * 100;
const riskColor = (data.risk_ratio ?? 0) >= 0.7 ? 'red' : 'green';

// HTML with CSS
const htmlReport = `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>Log Analysis Report</title>
  <style>
    body { font-family: "Segoe UI", Tahoma, sans-serif; background-color: #f8f9fa; padding: 20px; }
    h2, h3 { color: #444; text-align: center; }
    table { margin: 20px auto; border-collapse: collapse; width: 70%; max-width: 800px; background-color: #fff; 
      box-shadow: 0 2px 6px rgba(0,0,0,0.1); border-radius: 8px; overflow: hidden; }
    th, td { padding: 12px 15px; text-align: left; border-bottom: 1px solid #ddd; }
    th { background-color: #0066cc; color: #fff; }
    tr:nth-child(even) td { background-color: #f4f6f9; }
    tr:hover td { background-color: #eaf2ff; }
    .risk { font-weight: bold; }
  </style>
</head>
<body>
  <h2>Fortinet Log Analysis Report</h2>

  <!-- Main Metrics Table -->
  <table>
    <tr><th>Metric</th><th>Details</th></tr>
    <tr><td>Unique Users (source.ip)</td><td>${data.uniqueUsers ?? 'N/A'}</td></tr>
    <tr><td>Domains Accessed</td><td>${data.domains ?? 'N/A'}</td></tr>
    <tr><td>Start Time</td><td>${data.timeRange?.earliest ?? 'N/A'}</td></tr>
    <tr><td>End Time</td><td>${data.timeRange?.latest ?? 'N/A'}</td></tr>
    <tr><td>Risk (%)</td><td class="risk" style="color:${riskColor};">${riskPercent.toFixed(1)}%</td></tr>
    <tr><td>Judgement</td><td>${data.judgement ?? 'N/A'}</td></tr>
  </table>

  <!-- Summary Table -->
  <h3>Activity Summary</h3>
  <table>
    <tr><th>Summary</th></tr>
    <tr><td>${data.summary ?? 'No summary available'}</td></tr>
  </table>

</body>
</html>
`;

return [{ json: { htmlReport: htmlReport } }];
